function [numOfVars, numOfCoeff, X, C, D, J, Homotopy] = problems_optimalPnP_w_quaternion()
    % -- define the system -
    numOfVars = 4;
    numOfCoeff = 107;
    syms x_0 x_1 x_2 x_3
    X = [x_0 x_1 x_2 x_3];
