        D(36)*x_0^4 + D(37)*x_0^3*x_1 + D(38)*x_0^3*x_2 + D(39)*x_0^3*x_3 + D(40)*x_0^2*x_1^2 + D(41)*x_0^2*x_1*x_2 + D(42)*x_0^2*x_1*x_3 + D(43)*x_0^2*x_2^2 + D(44)*x_0^2*x_2*x_3 + D(45)*x_0^2*x_3^2 + D(46)*x_0*x_1^3 + D(47)*x_0*x_1^2*x_2 + D(48)*x_0*x_1^2*x_3 + D(49)*x_0*x_1*x_2^2 + D(50)*x_0*x_1*x_2*x_3 + D(51)*x_0*x_1*x_3^2 + D(52)*x_0*x_2^3 + D(53)*x_0*x_2^2*x_3 + D(54)*x_0*x_2*x_3^2 + D(55)*x_0*x_3^3 + D(56)*x_1^4 + D(57)*x_1^3*x_2 + D(58)*x_1^3*x_3 + D(59)*x_1^2*x_2^2 + D(60)*x_1^2*x_2*x_3 + D(61)*x_1^2*x_3^2 + D(62)*x_1*x_2^3 + D(63)*x_1*x_2^2*x_3 + D(64)*x_1*x_2*x_3^2 + D(65)*x_1*x_3^3 + D(66)*x_2^4 + D(67)*x_2^3*x_3 + D(68)*x_2^2*x_3^2 + D(69)*x_2*x_3^3 + D(70)*x_3^4
