    target_sys = [
        C(1)*x_0^4 + C(2)*x_0^3*x_1 + C(3)*x_0^3*x_2 + C(4)*x_0^3*x_3 + C(5)*x_0^2*x_1^2 + C(6)*x_0^2*x_1*x_2 + C(7)*x_0^2*x_1*x_3 + C(8)*x_0^2*x_2^2 + C(9)*x_0^2*x_2*x_3 + C(10)*x_0^2*x_3^2 + C(11)*x_0*x_1^3 + C(12)*x_0*x_1^2*x_2 + C(13)*x_0*x_1^2*x_3 + C(14)*x_0*x_1*x_2^2 + C(15)*x_0*x_1*x_2*x_3 + C(16)*x_0*x_1*x_3^2 + C(17)*x_0*x_2^3 + C(18)*x_0*x_2^2*x_3 + C(19)*x_0*x_2*x_3^2 + C(20)*x_0*x_3^3 + C(21)*x_1^4 + C(22)*x_1^3*x_2 + C(23)*x_1^3*x_3 + C(24)*x_1^2*x_2^2 + C(25)*x_1^2*x_2*x_3 + C(26)*x_1^2*x_3^2 + C(27)*x_1*x_2^3 + C(28)*x_1*x_2^2*x_3 + C(29)*x_1*x_2*x_3^2 + C(30)*x_1*x_3^3 + C(31)*x_2^4 + C(32)*x_2^3*x_3 + C(33)*x_2^2*x_3^2 + C(34)*x_2*x_3^3 + C(35)*x_3^4
