        C(71)*x_0^4 + C(72)*x_0^3*x_1 + C(73)*x_0^3*x_2 + C(74)*x_0^3*x_3 + C(75)*x_0^2*x_1^2 + C(76)*x_0^2*x_1*x_2 + C(77)*x_0^2*x_1*x_3 + C(78)*x_0^2*x_2^2 + C(79)*x_0^2*x_2*x_3 + C(80)*x_0^2*x_3^2 + C(81)*x_0*x_1^3 + C(82)*x_0*x_1^2*x_2 + C(83)*x_0*x_1^2*x_3 + C(84)*x_0*x_1*x_2^2 + C(85)*x_0*x_1*x_2*x_3 + C(86)*x_0*x_1*x_3^2 + C(87)*x_0*x_2^3 + C(88)*x_0*x_2^2*x_3 + C(89)*x_0*x_2*x_3^2 + C(90)*x_0*x_3^3 + C(91)*x_1^4 + C(92)*x_1^3*x_2 + C(93)*x_1^3*x_3 + C(94)*x_1^2*x_2^2 + C(95)*x_1^2*x_2*x_3 + C(96)*x_1^2*x_3^2 + C(97)*x_1*x_2^3 + C(98)*x_1*x_2^2*x_3 + C(99)*x_1*x_2*x_3^2 + C(100)*x_1*x_3^3 + C(101)*x_2^4 + C(102)*x_2^3*x_3 + C(103)*x_2^2*x_3^2 + C(104)*x_2*x_3^3 + C(105)*x_3^4
