function [numOfVars, numOfCoeff, X, C, D, J, Homotopy] = problems_refractive_p5p()
    % -- define the system -
    numOfVars = 5;
    numOfCoeff = 90;
    syms x_0 x_1 x_2 x_3 x_4
    X = [x_0 x_1 x_2 x_3 x_4];
    syms t;
    
    % -- coefficients --
    for i = 0:numOfCoeff-1
        % -- 1) target system --
        str_tgt_c = 'c_';
        cat_str_tgt_c = strcat(str_tgt_c, num2str(i));
        C(i+1) = str2sym(cat_str_tgt_c);

        % -- 2) start system --
        str_stt_c = 'd_';
        cat_str_stt_c = strcat(str_stt_c, num2str(i));
        D(i+1) = str2sym(cat_str_stt_c);
    end
    
    start_sys = [
        D(1)*x_0*x_2^2 + D(2)*x_0*x_2*x_3 + D(3)*x_0*x_2 + D(4)*x_0*x_3^2 + D(3)*x_0*x_3*x_4 + D(1)*x_0*x_4^2 + D(5)*x_0*x_4 + D(4)*x_0 + D(6)*x_1*x_2^2 + D(7)*x_1*x_2*x_3 + D(8)*x_1*x_2*x_4 + D(9)*x_1*x_3^2 + D(3)*x_1*x_3 + D(9)*x_1*x_4^2 + D(7)*x_1*x_4 + D(6)*x_1 + D(10)*x_2^2 + D(11)*x_2*x_3 + D(12)*x_2*x_4 + D(13)*x_2 + D(14)*x_3^2 + D(13)*x_3*x_4 + D(15)*x_3 + D(16)*x_4^2 + D(17)*x_4 + D(18);
        D(19)*x_0*x_2^2 + D(20)*x_0*x_2*x_3 + D(21)*x_0*x_2 + D(22)*x_0*x_3^2 + D(21)*x_0*x_3*x_4 + D(19)*x_0*x_4^2 + D(23)*x_0*x_4 + D(22)*x_0 + D(24)*x_1*x_2^2 + D(25)*x_1*x_2*x_3 + D(26)*x_1*x_2*x_4 + D(27)*x_1*x_3^2 + D(21)*x_1*x_3 + D(27)*x_1*x_4^2 + D(25)*x_1*x_4 + D(24)*x_1 + D(28)*x_2^2 + D(29)*x_2*x_3 + D(30)*x_2*x_4 + D(31)*x_2 + D(32)*x_3^2 + D(31)*x_3*x_4 + D(33)*x_3 + D(34)*x_4^2 + D(35)*x_4 + D(36);
        D(37)*x_0*x_2^2 + D(38)*x_0*x_2*x_3 + D(39)*x_0*x_2 + D(40)*x_0*x_3^2 + D(39)*x_0*x_3*x_4 + D(37)*x_0*x_4^2 + D(41)*x_0*x_4 + D(40)*x_0 + D(42)*x_1*x_2^2 + D(43)*x_1*x_2*x_3 + D(44)*x_1*x_2*x_4 + D(45)*x_1*x_3^2 + D(39)*x_1*x_3 + D(45)*x_1*x_4^2 + D(43)*x_1*x_4 + D(42)*x_1 + D(46)*x_2^2 + D(47)*x_2*x_3 + D(48)*x_2*x_4 + D(49)*x_2 + D(50)*x_3^2 + D(49)*x_3*x_4 + D(51)*x_3 + D(52)*x_4^2 + D(53)*x_4 + D(54);
        D(55)*x_0*x_2^2 + D(56)*x_0*x_2*x_3 + D(57)*x_0*x_2 + D(58)*x_0*x_3^2 + D(57)*x_0*x_3*x_4 + D(55)*x_0*x_4^2 + D(59)*x_0*x_4 + D(58)*x_0 + D(60)*x_1*x_2^2 + D(61)*x_1*x_2*x_3 + D(62)*x_1*x_2*x_4 + D(63)*x_1*x_3^2 + D(57)*x_1*x_3 + D(63)*x_1*x_4^2 + D(61)*x_1*x_4 + D(60)*x_1 + D(64)*x_2^2 + D(65)*x_2*x_3 + D(66)*x_2*x_4 + D(67)*x_2 + D(68)*x_3^2 + D(67)*x_3*x_4 + D(69)*x_3 + D(70)*x_4^2 + D(71)*x_4 + D(72);
        D(73)*x_0*x_2^2 + D(74)*x_0*x_2*x_3 + D(75)*x_0*x_2 + D(76)*x_0*x_3^2 + D(75)*x_0*x_3*x_4 + D(73)*x_0*x_4^2 + D(77)*x_0*x_4 + D(76)*x_0 + D(78)*x_1*x_2^2 + D(79)*x_1*x_2*x_3 + D(80)*x_1*x_2*x_4 + D(81)*x_1*x_3^2 + D(75)*x_1*x_3 + D(81)*x_1*x_4^2 + D(79)*x_1*x_4 + D(78)*x_1 + D(82)*x_2^2 + D(83)*x_2*x_3 + D(84)*x_2*x_4 + D(85)*x_2 + D(86)*x_3^2 + D(85)*x_3*x_4 + D(87)*x_3 + D(88)*x_4^2 + D(89)*x_4 + D(90);
    ];

    target_sys = [
        C(1)*x_0*x_2^2 + C(2)*x_0*x_2*x_3 + C(3)*x_0*x_2 + C(4)*x_0*x_3^2 + C(3)*x_0*x_3*x_4 + C(1)*x_0*x_4^2 + C(5)*x_0*x_4 + C(4)*x_0 + C(6)*x_1*x_2^2 + C(7)*x_1*x_2*x_3 + C(8)*x_1*x_2*x_4 + C(9)*x_1*x_3^2 + C(3)*x_1*x_3 + C(9)*x_1*x_4^2 + C(7)*x_1*x_4 + C(6)*x_1 + C(10)*x_2^2 + C(11)*x_2*x_3 + C(12)*x_2*x_4 + C(13)*x_2 + C(14)*x_3^2 + C(13)*x_3*x_4 + C(15)*x_3 + C(16)*x_4^2 + C(17)*x_4 + C(18);
        C(19)*x_0*x_2^2 + C(20)*x_0*x_2*x_3 + C(21)*x_0*x_2 + C(22)*x_0*x_3^2 + C(21)*x_0*x_3*x_4 + C(19)*x_0*x_4^2 + C(23)*x_0*x_4 + C(22)*x_0 + C(24)*x_1*x_2^2 + C(25)*x_1*x_2*x_3 + C(26)*x_1*x_2*x_4 + C(27)*x_1*x_3^2 + C(21)*x_1*x_3 + C(27)*x_1*x_4^2 + C(25)*x_1*x_4 + C(24)*x_1 + C(28)*x_2^2 + C(29)*x_2*x_3 + C(30)*x_2*x_4 + C(31)*x_2 + C(32)*x_3^2 + C(31)*x_3*x_4 + C(33)*x_3 + C(34)*x_4^2 + C(35)*x_4 + C(36);
        C(37)*x_0*x_2^2 + C(38)*x_0*x_2*x_3 + C(39)*x_0*x_2 + C(40)*x_0*x_3^2 + C(39)*x_0*x_3*x_4 + C(37)*x_0*x_4^2 + C(41)*x_0*x_4 + C(40)*x_0 + C(42)*x_1*x_2^2 + C(43)*x_1*x_2*x_3 + C(44)*x_1*x_2*x_4 + C(45)*x_1*x_3^2 + C(39)*x_1*x_3 + C(45)*x_1*x_4^2 + C(43)*x_1*x_4 + C(42)*x_1 + C(46)*x_2^2 + C(47)*x_2*x_3 + C(48)*x_2*x_4 + C(49)*x_2 + C(50)*x_3^2 + C(49)*x_3*x_4 + C(51)*x_3 + C(52)*x_4^2 + C(53)*x_4 + C(54);
        C(55)*x_0*x_2^2 + C(56)*x_0*x_2*x_3 + C(57)*x_0*x_2 + C(58)*x_0*x_3^2 + C(57)*x_0*x_3*x_4 + C(55)*x_0*x_4^2 + C(59)*x_0*x_4 + C(58)*x_0 + C(60)*x_1*x_2^2 + C(61)*x_1*x_2*x_3 + C(62)*x_1*x_2*x_4 + C(63)*x_1*x_3^2 + C(57)*x_1*x_3 + C(63)*x_1*x_4^2 + C(61)*x_1*x_4 + C(60)*x_1 + C(64)*x_2^2 + C(65)*x_2*x_3 + C(66)*x_2*x_4 + C(67)*x_2 + C(68)*x_3^2 + C(67)*x_3*x_4 + C(69)*x_3 + C(70)*x_4^2 + C(71)*x_4 + C(72);
        C(73)*x_0*x_2^2 + C(74)*x_0*x_2*x_3 + C(75)*x_0*x_2 + C(76)*x_0*x_3^2 + C(75)*x_0*x_3*x_4 + C(73)*x_0*x_4^2 + C(77)*x_0*x_4 + C(76)*x_0 + C(78)*x_1*x_2^2 + C(79)*x_1*x_2*x_3 + C(80)*x_1*x_2*x_4 + C(81)*x_1*x_3^2 + C(75)*x_1*x_3 + C(81)*x_1*x_4^2 + C(79)*x_1*x_4 + C(78)*x_1 + C(82)*x_2^2 + C(83)*x_2*x_3 + C(84)*x_2*x_4 + C(85)*x_2 + C(86)*x_3^2 + C(85)*x_3*x_4 + C(87)*x_3 + C(88)*x_4^2 + C(89)*x_4 + C(90);
    ];

    % -- construct the hpmotopy via the scalar variable t --
    Homotopy = start_sys * (1-t) + target_sys * t;

    % -- compute the Jacobian matrix --
    J = jacobian(Homotopy, [x_0 x_1 x_2 x_3 x_4 t]);
end